import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Validators } from '@angular/forms';

@Component({
  selector: 'app-reactive',
  templateUrl: './reactive.component.html',
  styleUrls: ['./reactive.component.css'],
})
export class ReactiveComponent {
  productForm = new FormGroup({
    productid: new FormControl(0, [
      Validators.required,
      Validators.min(1000),
      Validators.max(9999),
    ]),
    brand: new FormControl('', Validators.maxLength(20)),
    description: new FormControl(''),
    qty: new FormControl(0),
    price: new FormControl(0),
  });

  onSubmit() {
    console.error(this.productForm);
    console.warn(this.productForm.value);
    console.log(this.productForm.controls['price'].value);
  }
}
<h2>Product Form - Reactive</h2>
<div class="container">
  <form [formGroup]="productForm" (ngSubmit)="onSubmit()"  >
    <label for="">ProductId: </label>
    <input type="text" formControlName="productid" />

    <label for="">Brand</label>

    <input type="text" formControlName="brand" />

    <label for="">Description</label>

    <input type="text" formControlName="description" />

    <label for="qty">Quantity</label>
    <input type="text" formControlName="qty" />

    <label for="">Price</label>
    <input type="text" formControlName="price" />

    <button type="submit" [disabled]="!productForm.valid">Submit</button>
  </form>
</div>
